import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Validation from "./LoginValidation";
import axios from "axios";

function Login (){

    const [values, setValues] = useState({
        email : '',
        password : ''
    })

    const [errors,setError] = useState({})

    const navigate = useNavigate()

    const handleSubmit = (e) => {
        e.preventDefault();
        setError(Validation(values))

        if(errors.email === "" && errors.password === ""){
            axios.post("http://localhost:2023/login",values).then(res => {
                console.log(res)
                if(res.data === "Success"){
                    navigate("/home")
                }
                else{
                    alert("No Such User Exist!!")
                }
            })
            .catch(err => {
                console.log(err)
            })
        }
    }

    const handleInput = (e) => {
        setValues(prevValue => ({...prevValue, [e.target.name] : [e.target.value]}))
    }

    return (
        <div className="d-flex justify-content-end align-items-start bg-black vh-100">
    {/* Right Side - Login Form */}
    <div className="right-panel bg-white p-3 rounded w-25 mt-3 mr-3">
        <h2>Login Form</h2>
        <form action="" onSubmit={handleSubmit}>
            <div className="mb-3">
                <label htmlFor="email"><strong>Email</strong></label>
                <input type="email" name="email" placeholder="Enter Your Email" onChange={handleInput} className="form-control rounded-0" />
                {errors.email && <span className="text-danger">{errors.email}</span>}
            </div>
            <div className="mb-3">
                <label htmlFor="password"><strong>Password</strong></label>
                <input type="password" name="password" placeholder="Enter Your Password" onChange={handleInput} className="form-control rounded-0" />
                {errors.password && <span className="text-danger">{errors.password}</span>}
            </div>
            <button type="submit" className="btn btn-success w-100">Login</button>
            <p>You agree to our terms and policies</p>
            <Link to="/signup" className="btn btn-default border w-100 bg-light">Create Account</Link>
        </form>
    </div>

    {/* Left Side - Picture */}
    <div className="left-panel">
        <img
            src="https://pbs.twimg.com/profile_images/1683498543967879173/EHRSRyrp_400x400.jpg"  // Replace with your image URL
            alt="Login Background"
            className="img-fluid"
        />
    </div>
</div>

    )
}

export default Login
